import React, { useState } from 'react';
import Form from 'react-bootstrap/Form'
import './login.css'
// import AlertDismissibleExample from '../../Components/alertDismissibleExample';


const Login = props => {
    const [name, setName] = useState('')
    const [gender, setGender] = useState('')
    const [massage, setmassage] = useState([])
    //methods
    let checkForInter = state => {
        console.log(state.name);
        if (!state.name || state.name ==='') {
            return [{ 0: false, 1: '1', pm: 'لطفا اسم خود را به درستی وارد کنید' }]
        }
        else if (!state.gender || state.gender === '0') {
            return [{ 0: false, 1: '0', pm: 'لطفا جنسیت خود را انتخاب کنید' }]
        }
        return [true];
    }



    let inputHander = e => {
        e.preventDefault();
        setmassage(checkForInter({ name, gender }))
        console.log(massage);

        setName('')
    }

    return (
        <div>






            <Form className='formforInpur' onSubmit={inputHander}>
                <div className=' text-center  mb-5 '>
                    <h2 className='headertext'>چت روم علی </h2>
                </div>

                <input className='p-2' onChange={e => setName(e.target.value)} value={name}
                    placeholder='...لطفا اسم خود را وارد کنید'
                />
                <Form.Control
                    as="select"
                    className=" my-4 "
                    id="inlineFormCustomSelect"
                    onChange={e => setGender(e.target.value)}
                    custom>

                    <option value="0">انتخاب...</option>
                    <option value="1">زن</option>
                    <option value="2">مرد</option>
                </Form.Control>
                <button type='submit' className='btn btn-sm btn-danger'>کلیک کنید </button>

            </Form>
        </div>

    )
}

export default Login
