import React from 'react'


function BodyChat(porps) {
    return (
        <>
            {
                messages.map(item => {

                    return (
                        <div className={item.state.name === props.location.state.name ? "imgDiv" : "imgDivReverc"} key={item.key}>

                            <img src={item.state.gender === "0" ? `${male_user}` : `${famle_user}`} title={`${item.state.name}`} className="imgUser" alt="img" />
                            <Card.Text className={item.state.name === props.location.state.name ? "textBorder" : "speechbubble"} >


                                <span> {item.state.name}</span>
                                <span className="messageText">{item.mes}</span>



                            </Card.Text>
                        </div>
                    )
                })
            }
        </>
    )
}

export default BodyChat
