function AppReducer(state, action) {
    let { name, gender, propsLogin } = action.paylode

    switch (action.type) {

        case 'add_profile':
            console.log(name)
            return inputHander({ name, gender, propsLogin })
        case 'add_text':
            return action.paylode



        default:
            return state
    }
}

export default AppReducer;

function checkOut({ name, gender }) {
    if (!name) {
        let erroName = [{ id: 0, massage: 'لطفا نام خود را وارد کنید' }]
        return 'al'
        // setmassage(erroName)
    }
    else if (gender === '-1') {
        let erroGender = [{ id: 1, massage: 'لطفا جنسیت خود را انتخاب کنید' }]
        return erroGender
        // setmassage(erroGender)
    }
    else {
        let erroGender = [{ id: 3, massage: 'خوش امدید به چت روم علی ' }]
        return erroGender

        // setmassage(erroGender)
    }
}


let inputHander = ({ name, gender, propsLogin }) => {
    checkOut({ name, gender })
    propsLogin.history.push({
        pathname: "chatroom",
        state: {
            name,
            gender,
        }
    });


}
