import React, { useEffect, useState } from 'react'
import SocketIOClient from 'socket.io-client'
import Card from 'react-bootstrap/Card'
import './chatRoom.css'
import male_user from './img/male_user.png'
// import famle_user from './img/women(1).png'


const ChatRoom = props => {
    const massage = [
        { id: Date.now(), mes: 'سلام', state: { name: props.location.state.gender, gender: props.location.state.gender } },
        { id: Date.now(), mes: 'خویی', state: { name: props.location.state.gender, gender: props.location.state.gender } },
        { id: Date.now(), mes: 'مرسی', state: { name: props.location.state.gender, gender: props.location.state.gender } }
    ]
    const [text, setText] = useState('')
    const socket = React.useRef(SocketIOClient.connect("http://localhost:3010/socket"))
    console.log(socket);
    // useEffect(() => {

    // }, [])
    return (
        <div className='container'>
            <div className="row">
                <div className='col'>
                    <Card className="cardChat ">
                        <Card.Header className="cardtitle"> صفحه اصلی چت روم</Card.Header>
                        <Card.Body className="cardBody">


                            {
                                massage.map(item => {
                                    return (
                                        <div className='imgDiv'>

                                            <img src={male_user} alt='woemn' title='زن' className="imgUser" />
                                            <Card.Text className="textBorder">
                                             {   item.mes}
                                            </Card.Text>
                                        </div>
                                    )
                                })
                            }




                        </Card.Body>
                        <Card.Footer className="d-flex cardFooter justify-content-between" >
                            <input className="input1" value={text} placeholder={`  تایپ کنید ...`} />
                            <button className=" buttonA">
                                <svg width="1em" height="1em" viewBox="0 0 16 16" class="bi bi-cursor-fill" fill="currentColor" xmlns="http://www.w3.org/2000/svg">
                                    <path fill-rule="evenodd" d="M14.082 2.182a.5.5 0 0 1 .103.557L8.528 15.467a.5.5 0 0 1-.917-.007L5.57 10.694.803 8.652a.5.5 0 0 1-.006-.916l12.728-5.657a.5.5 0 0 1 .556.103z" />
                                </svg>
                            </button>

                        </Card.Footer>
                    </Card>
                </div>
            </div>
        </div>
    )
}

export default ChatRoom
