import React, { useEffect, useState } from 'react'
import SocketIOClient from 'socket.io-client'
import Card from 'react-bootstrap/Card'
import './chatRoom.css'
import male_user from './img/male_user.png'
import famle_user from './img/woman(1).png'


const ChatRoom = props => {
    // const massage = [
    //     { id: Date.now(), mes: 'سلام', state: { name: props.location.state.name, gender: props.location.state.gender } },
    //     { id: Date.now(), mes: 'خویی', state: { name: props.location.state.name, gender: props.location.state.gender } },
    //     { id: Date.now(), mes: 'خویی', state: { name: props.location.state.name, gender: props.location.state.gender } },
    //     { id: Date.now(), mes: 'خویی', state: { name: props.location.state.name, gender: props.location.state.gender } },
    //     { id: Date.now(), mes: 'خویی', state: { name: "زیبا", gender:"0" } },
    //     { id: Date.now(), mes: 'خویی', state: { name: props.location.state.name, gender: props.location.state.gender } },
    //     { id: Date.now(), mes: 'خویی', state: { name: props.location.state.name, gender: props.location.state.gender } },
    //     { id: Date.now(), mes: 'مرسی', state: { name: props.location.state.name, gender: props.location.state.gender } }
    // ]
    const [messages, setMessages] = useState([])
    const [text, setText] = useState([])
    const socket = React.useRef(SocketIOClient.connect("http://localhost:3010/socket"))
    let sumbit = e => {
        e.preventDefault()
        socket.current.emit("newMessage", {
            id: "",
            date:new Date(),
            mes: text,
            state: {
                name: props.location.state.name,
                gende: props.location.state.gender
            }
        })
    }


    useEffect(() => {
        socket.current.on("newMessage", (message) => {
            console.log(message);
            setMessages(messages => messages.concat(message));
        })

    }, [])

    return (
        <div className='container'>
            <div className="row">
                <div className='col'>
                    <Card className="cardChat ">
                        <Card.Header className="cardtitle"> صفحه اصلی چت روم</Card.Header>
                        <Card.Body className="cardBody">


                            {
                                messages.map(item => {
                                             console.log(item , 's');
                                    return (
                                        <div className={item.state.name === props.location.state.name ? "imgDiv" : "imgDivReverc"}  >

                                            <img src={item.state.gender == "0" ? `${male_user}` : `${famle_user}`} title={`${item.state.name}`} className="imgUser" alt="img" />
                                            <Card.Text className="textBorder">
                                                {item.mes}

                                            </Card.Text>
                                        </div>
                                    )
                                })
                            }




                        </Card.Body>
                        <Card.Footer className="d-flex cardFooter justify-content-between" >
                            <input type="text" className="input1" onChange={e => setText(e.target.value)} value={text} placeholder={`  تایپ کنید ...`} />
                            <button className=" buttonA" onClick={sumbit}>
                                <svg width="1em" height="1em" viewBox="0 0 16 16" className="bi bi-cursor-fill" fill="currentColor" xmlns="http://www.w3.org/2000/svg">
                                    <path fillRule="evenodd" d="M14.082 2.182a.5.5 0 0 1 .103.557L8.528 15.467a.5.5 0 0 1-.917-.007L5.57 10.694.803 8.652a.5.5 0 0 1-.006-.916l12.728-5.657a.5.5 0 0 1 .556.103z" />
                                </svg>
                            </button>

                        </Card.Footer>
                    </Card>
                </div>
            </div>
        </div>
    )
}

export default ChatRoom
