import React, { useState } from 'react';

import Form from 'react-bootstrap/Form'
import Button from 'react-bootstrap/Button'

function Login() {
    const [name, setName] = useState('')
    const [fale, setfale] = useState('')

    let checkNameAndFale = (check) => {
        if (!check.name) {
            return 'لطفا  نام خود را وارد کنید'
        }
        else if (check.fale === '0' || check.fale === '') return 'لطفا جنسیت خود را وارد کنید'
        return true
    }
    let logHander = e => {
        e.preventDefault()
        console.log(checkNameAndFale({ name, fale }));
        setName('')

    }


    return (
        <>
            <div style={{
                textAlign: 'center',
                margin: ' 2rem 0 4rem 0'
            }}>


                <h2 > چت روم علی</h2>
            </div>

            <div style={{ textAlign: 'right', display: 'flex', justifyContent: 'center' }} className='  container' >
                <div>
                    <Form onSubmit={logHander} >
                        <Form.Group
                            style={{ maxWidth: '200px' }}
                            controlId="formBasicEmail"
                        >

                            <Form.Control
                                type="text"
                                value={name}
                                onChange={e => setName(e.target.value)}
                                placeholder="لطفا وارد کنید ..."
                            />


                        </Form.Group>

                        <Form.Control
                            style={{ maxWidth: '200px' }}
                            as="select"
                            className=" mb-2"
                            id="inlineFormCustomSelect"
                            onChange={e => setfale(e.target.value)}
                            custom
                        >
                            <option value="0">انتخاب...</option>
                            <option value="1">زن</option>
                            <option value="2">مرد</option>
                        </Form.Control>
                        <br />


                        <Button style={{ maxWidth: '80px' }} variant="danger" type="submit">
                            Submit
    </Button>
                    </Form>
                </div>
            </div>
        </>
    );
}

export default Login;
