import React, { useState } from 'react';
import Form from 'react-bootstrap/Form'
import Button from 'react-bootstrap/Button'
import './login.css'



const Login = props => {
    const [name, setName]=useState('')
    return (
        <div>
            <Form className='formforInpur'>
                <input onChange={e => setName(e.target.value)} value={name} placeholder='...لطفا اسم خود را وارد کنید'/>
                <button className='btn btn-sm btn-danger'>کلیک کنید </button>

            </Form>
        </div>

    )
}

export default Login
