import React from 'react';
import 'bootstrap/dist/css/bootstrap.css';
import './css/style.css'
import Form from 'react-bootstrap/Form'
import Button from 'react-bootstrap/Button'

function App() {
  return (
    <div style={{textAlign:'right',maxWidth:'300px,' , height:'auto'}}  className=' container d-flex flex-column aligin justify-content-center'>
      <h2 style={{color:'red' }}> چت روم علی</h2>


      <Form >
        <Form.Group controlId="formBasicEmail">

          <Form.Control type="text" placeholder="لطفا وارد کنید ..." />
          <Form.Text className=" text-muted">
            لطفا اسم خود را درست وارد کنید
      </Form.Text>
        </Form.Group>
        <Form.Control
          as="select"
          className="mr-sm-2 mb-2"
          id="inlineFormCustomSelect"
          custom
        >
          <option value="0">انتخاب...</option>
          <option value="1">زن</option>
          <option value="2">مرد</option>
        </Form.Control>


        <Button ClassName='w-25' variant="primary" type="submit">
          Submit
    </Button>
      </Form>
    </div>
  );
}

export default App;
