import React, { useState, useReducer } from 'react';
//import css && bootstrap
import Form from 'react-bootstrap/Form'
import './login.css'
//import component
import AlertDismissibleExample from '../../Components/alertDismissibleExample';
//import context
import LoginContext from '../../context/loginContext'
//import Reducer
import AppReducer from './../../reducers/appReducer'


const Login = props => {
    const [state, dispatch] = useReducer( AppReducer , {
        name:'',
        gender:'',
        message:[]
    } )
    const [name, setName] = useState('')
    const [gender, setGender] = useState('0')
    const [massage, setmassage] = useState([])
    //methods
    function checkOut({ name, gender }) {
        if (!name) {
            let erroName = [{ id: 0, massage: 'لطفا نام خود را وارد کنید' }]
            setmassage(erroName)
        }
        else if (gender === '-1') {
            let erroGender = [{ id: 1, massage: 'لطفا جنسیت خود را انتخاب کنید' }]
            setmassage(erroGender)
        }
        else {
            let erroGender = [{ id: 3, massage: 'خوش امدید به چت روم علی ' }]
            setmassage(erroGender)
        }
    }


    let inputHander = e => {
        props.history.push({
            pathname: "chatroom",
            state: {
                name,
                gender,
            }
        });
        e.preventDefault()
        checkOut({ name, gender })

    }




    return (
        <div>
            {
                massage.map(item => {
                    return (
                        <AlertDismissibleExample name={name} id={item.id} massage={item.massage} />
                    )
                })
            }






            <Form className='formforInpur' onSubmit={inputHander}>
                <div className=' text-center  mb-5 '>
                    <h2 className='headertext'>چت روم علی </h2>
                </div>

                <input className='p-2' onChange={e => setName(e.target.value)} value={name}
                    placeholder='...لطفا اسم خود را وارد کنید'
                />
                <Form.Control
                    as="select"
                    className=" my-4 "
                    id="inlineFormCustomSelect"
                    onChange={e => setGender(e.target.value)}
                    custom>

                    <option value="-1">انتخاب...</option>
                    <option value="1">زن</option>
                    <option value="0">مرد</option>
                </Form.Control>
                <button type='submit' className='btn btn-sm btn-danger'>کلیک کنید </button>

            </Form>
        </div>

    )
}

export default Login
